import React from "react";
import { useState } from "react";
import { useTranslation } from "next-i18next";
import { useForm } from "react-hook-form";

// import { useUI } from '@contexts/ui.context';
import UploadImage from "src/pages/upload-image";
import Button from "@components/ui/button";
import Input from "@components/ui/input";
import Counter from "@components/common/counter";

type addProductType = {
  mainImage: string;
  image: string;
  productName: string;
  productPrice: number;
  salePrice: number;
  size: string;
  color: string;
  quantity: number;
  description: string;
};

const AddProductForm = () => {
  const { t } = useTranslation();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<addProductType>();

  // const { closeModal } = useUI();

  const [quantity, setQuantity] = useState(1);
  const onChangeHandler = (event: any) => {
    const { value } = event.target;

    setQuantity(value);
  };

  const onSubmit = (data: any) => {
    const punctuationRegex = /[!"#$%&'()*+,-./:;<=>?@[\]^_`{|}~]/g;
    let slug = data.productName
      .replace(punctuationRegex, "")
      .toLowerCase()
      .replace(/\s/g, "-");
    data.slug = slug;
    data.quantity = quantity;
    console.log(data);
  };

  return (
    <>
      <div className="bg-white px-6">
        <div className="cart-body py-7">
          <div className="add-product-content ">
            <form
              className="grid grid-cols-1 lg:grid-cols-2 gap-3"
              onSubmit={handleSubmit(onSubmit)}
            >
              <div className="add-product-image mb-3 lg:mb-0">
                <div className="add-image">
                  <div className="grid grid-cols-12 items-center">
                    <div className="col-span-9 relative">
                      {/* <input type="file" name="upload-img-main" className='absolute left-0 right-0 top-0 bottom-0 opacity-0' /> */}
                      {/* <img src="https://react.pixelstrap.com/multikart-admin/static/media/1.45e56070.jpg" alt="image" className='max-w-full h-auto' /> */}
                      <UploadImage multiple={false} />
                    </div>

                    <div className="col-span-3">
                      <ul className="file-upload-product">
                        <li className="flex mb-3.5 items-center justify-center">
                          <div className="box-input-file w-12 h-12 bg-gray-300 flex items-center relative">
                            <input
                              type="file"
                              name="upload-img-gallery"
                              className="upload absolute w-12 left-0 right-0 opacity-0 h-12"
                            />
                            <img
                              className="w-12 h-12"
                              src="data:image/png;base64,"
                              alt="img-upload"
                            />
                          </div>
                        </li>

                        <li className="flex mb-3.5 items-center justify-center">
                          <div className="box-input-file w-12 h-12 bg-gray-300 flex items-center relative">
                            <input
                              type="file"
                              name="upload-img-gallery"
                              className="upload absolute w-12 left-0 right-0 opacity-0 h-12"
                            />
                            <img
                              className="w-12 h-12"
                              src="data:image/png;base64,"
                              alt="img-upload"
                            />
                          </div>
                        </li>

                        <li className="flex mb-3.5 items-center justify-center">
                          <div className="box-input-file w-12 h-12 bg-gray-300 flex items-center relative">
                            <input
                              type="file"
                              name="upload-img-gallery"
                              className="upload absolute w-12 left-0 right-0 opacity-0 h-12"
                            />
                            <img
                              className="w-12 h-12"
                              src="data:image/png;base64,"
                              alt="img-upload"
                            />
                          </div>
                        </li>

                        <li className="flex mb-3.5 items-center justify-center">
                          <div className="box-input-file w-12 h-12 bg-gray-300 flex items-center relative">
                            <input
                              type="file"
                              name="upload-img-gallery"
                              className="upload absolute w-12 left-0 right-0 opacity-0 h-12"
                            />
                            <img
                              className="w-12 h-12"
                              src="data:image/png;base64,"
                              alt="img-upload"
                            />
                          </div>
                        </li>

                        <li className="flex mb-3.5 items-center justify-center">
                          <div className="box-input-file w-12 h-12 bg-gray-300 flex items-center relative">
                            <input
                              type="file"
                              name="upload-img-gallery"
                              className="upload absolute w-12 left-0 right-0 opacity-0 h-12"
                            />
                            <img
                              className="w-12 h-12"
                              src="data:image/png;base64,"
                              alt="img-upload"
                            />
                          </div>
                        </li>

                        <li className="flex mb-3.5 items-center justify-center">
                          <div className="box-input-file w-12 h-12 bg-gray-300 flex items-center relative">
                            <input
                              type="file"
                              name="upload-img-gallery"
                              className="upload absolute w-12 left-0 right-0 opacity-0 h-12"
                            />
                            <img
                              className="w-12 h-12"
                              src="data:image/png;base64,"
                              alt="img-upload"
                            />
                          </div>
                        </li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>

              <div className="add-product-information ">
                <div className="form-content">
                  <div className="mb-6">
                    {/* <label htmlFor="product_name" className='w-5/12'>{t('forms:label-product-name')} </label>
                                        <input name='product_name' id="product_name" type="text" className='text-sm rounded px-3 py-2 w-full border border-solid border-gray-400' /> */}
                    <Input
                      labelKey={t("forms:label-product-name")}
                      type="text"
                      variant="solid"
                      {...register("productName", {
                        required: {
                          value: true,
                          message: `${t("forms:product-name-required")}`,
                        },
                      })}
                      errorKey={errors.productName?.message}
                      formAdd={true}
                      className="text-sm rounded px-3 py-2 w-full border border-solid border-gray-400"
                    />
                  </div>

                  <div className="mb-6">
                    {/* <label htmlFor="product_price" className='w-5/12'>{t('forms:label-price')} </label> */}
                    {/* <input name='price' id="product_price" type="number" className='text-sm rounded px-3 py-2 w-full border border-solid border-gray-400' /> */}
                    <Input
                      labelKey={t("forms:label-price")}
                      type="number"
                      variant="solid"
                      {...register("productPrice", {
                        required: `${t("forms:product-price-required")}`,
                      })}
                      errorKey={errors.productPrice?.message}
                      formAdd={true}
                      className="text-sm rounded px-3 py-2 w-full border border-solid border-gray-400"
                    />
                  </div>

                  <div className="mb-6">
                    <Input
                      labelKey={t("forms:label-sale-price")}
                      type="number"
                      variant="solid"
                      {...register("salePrice", {
                        required: `${t("forms:sale-price-required")}`,
                      })}
                      errorKey={errors.salePrice?.message}
                      formAdd={true}
                      defaultValue={0}
                      className="text-sm rounded px-3 py-2 w-full border border-solid border-gray-400"
                    />
                  </div>

                  <div className="mb-6">
                    <Input
                      labelKey={t("forms:label-select-size")}
                      type="text"
                      variant="solid"
                      {...register("size", {
                        required: `${t("forms:size-required")}`,
                      })}
                      errorKey={errors.size?.message}
                      formAdd={true}
                      className="text-sm rounded px-3 py-2 w-full border border-solid border-gray-400"
                    />
                  </div>

                  <div className="mb-6">
                    <Input
                      labelKey={t("forms:label-color")}
                      type="text"
                      variant="solid"
                      {...register("color", {
                        required: `${t("forms:color-required")}`,
                      })}
                      errorKey={errors.color?.message}
                      formAdd={true}
                      className="text-sm rounded px-3 py-2 w-full border border-solid border-gray-400"
                    />
                  </div>

                  <div className="md:flex block items-center mb-6 justify-center">
                    <label htmlFor="total_products" className="w-5/12">
                      {t("forms:label-total-product")}{" "}
                    </label>
                    <div className="text-sm">
                      <Counter
                        quantity={quantity}
                        onIncrement={() =>
                          setQuantity((prev) => Number(prev) + 1)
                        }
                        onDecrement={() =>
                          setQuantity((prev) => (prev > 1 ? prev - 1 : 1))
                        }
                        disableDecrement={quantity === 1}
                        isInput={true}
                        onValueChange={onChangeHandler}
                        // {...register("quantity", {
                        //     required: true,
                        // })}
                      />
                    </div>
                  </div>

                  <div className="md:flex block items-center justify-center">
                    <label htmlFor="description" className="w-5/12">
                      {t("forms:label-description")}
                    </label>
                    <textarea
                      id="description"
                      className="text-sm px-3 py-2 w-full border border-solid border-gray-400 rounded"
                      cols={30}
                      rows={10}
                      defaultValue={""}
                      {...register("description", {
                        required: true,
                      })}
                    />
                  </div>
                  {errors.description && (
                    <p className="my-2 text-xs text-right text-red-500">
                      {t("forms:description-required")}
                    </p>
                  )}
                </div>

                <div className="xl:ml-3 sm:ml-4 mt-6">
                  <Button
                    type="submit"
                    className="h-11 md:h-12 mt-1.5 bg-[#ff8084] hover:bg-[#ff5059]"
                  >
                    {t("common:button-add")}
                  </Button>

                  <Button
                    type="reset"
                    variant="flat"
                    className="h-11 md:h-12 mt-1.5 ml-3 bg-gray-400 hover:bg-gray-400"
                    // disabled
                  >
                    {t("common:button-reset")}
                  </Button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default AddProductForm;
